    import { useState } from 'react';

    import { Button, Box, styled } from '@mui/material';
    import { ShoppingCart as Cart, FlashOn as Flash } from '@mui/icons-material';
    import axios from 'axios';
    import { useNavigate } from 'react-router-dom';
    import { payUsingPaytm } from '../../service/api';
    import { post } from '../../utils/paytm';
    import ProductDetail from './ProductDetail';
    import { useDispatch } from 'react-redux';
    import { addToCart } from '../../redux/actions/cartActions';

    const URL='http://localhost:8000'

    const LeftContainer = styled(Box)(({ theme }) => ({
        minWidth: '40%',
        padding: '40px 0 0 80px',
        [theme.breakpoints.down('md')]: {
            padding: '20px 40px'
        }
    }));

    const Image = styled('img')({
        padding: '15px 20px',
        border: '1px solid #f0f0f0',
        width: '100%' // Ensure it takes full width of its container
    });

    const StyledButton = styled(Button)`
        width: 46%;
        border-radius: 2px;
        height: 50px;
        color: #FFF;
    `;

    const ActionItem = ({ product }) => {
        const navigate = useNavigate();
        const { id } = product;
            
        const [quantity, setQuantity] = useState(1);
        const dispatch = useDispatch();


        const loadRazorpay = async () => {
            return new Promise((resolve) => {
                const script = document.createElement("script");
                script.src = "https://checkout.razorpay.com/v1/checkout.js";
                script.onload = () => resolve(true);
                script.onerror = () => resolve(false);
                document.body.appendChild(script);
            });
        };



        const buyNow = async () => {
            try {
                console.log("üü¢ Buy Now Button Clicked");
        
                // ‚úÖ Step 1: Load Razorpay SDK
                const razorpayLoaded = await loadRazorpay();
                if (!razorpayLoaded) {
                    console.error("‚ùå Razorpay SDK failed to load. Check your internet connection.");
                    return;
                }
        
                // ‚úÖ Step 2: Get API Key
                const RAZORPAY_KEY = process.env.RAZORPAY_KEY || "rzp_test_V3I3xCijmk7n0s";
                console.log("üîë Razorpay Key:", RAZORPAY_KEY);
        
                // ‚úÖ Step 3: Create Order via Backend
                const { data } = await axios.post(`${URL}/payment`, {
                    amount: 500,
                    currency: 'INR'
                });
        
                console.log("üì• API Response:", data);
        
                if (!data.success || !data.order || !data.order.id) {
                    console.error("‚ùå Failed to create order:", data.error);
                    return;
                }
        
                const { order } = data; 
                console.log("üìå Order Details from Backend:", order);
        
                // ‚úÖ Step 4: Ensure Order ID Exists
                if (!order.id) {
                    console.error("‚ùå Order ID is missing! Cannot proceed.");
                    return;
                }
        
                // ‚úÖ Step 5: Initialize Razorpay Options
                const options = {

                    key: process.env.RAZORPAY_KEY,  // Use dynamic key from .env
                    amount: order.amount,
                    currency: order.currency,
                    name: "xyz",
                    description: "Test Transaction",
                    image: "data:image/jpeg;base64,",  // Replace with your logo
                    order_id: order.id, // Razorpay Order ID
                    handler: async function (response) {
                        console.log("‚úÖ Payment Success Response:", response);
        
                        // ‚úÖ Step 6: Verify Payment via Backend
                        try {
                            console.log("entered try block:");
                            const verifyRes = await axios.post(`${URL}/verify`, {
                                order_id: order.id,
                                payment_id: response.razorpay_payment_id,
                                signature: response.razorpay_signature,
                            });
        
                            console.log("üîç Payment Verification Response:", verifyRes.data);
                            
                            if (verifyRes.data.success) {
                                alert("üéâ Payment Successful!");
                            } else {
                                alert("‚ö†Ô∏è Payment verification failed!");
                            }
                        } catch (error) {
                            console.error("‚ùå Payment Verification Failed:", error.response?.data || error.message);
                            alert("‚ùå Error verifying payment");
                        }
                    },
                    prefill: {
                        name: "John Doe",
                        email: "johndoe@example.com",
                        contact: "9999999999"
                    },
                    theme: {
                        color: "#3399cc"
                    }
                };
        
                console.log("üöÄ Opening Razorpay Checkout...");
                const rzp = new window.Razorpay(options);
                rzp.open();
        
            } catch (error) {
                console.error("‚ùå API Error:", error.response?.data || error.message);
            }
        };
        
        
        const addItemToCart = () => {
            console.log("Adding to cart:", { id, quantity });  // Debugging
            dispatch(addToCart(id, quantity));
            navigate('/cart');
        };
        
        
        return (
            <LeftContainer>
                <Box sx={{ padding: '10px 50px', border: '1px solid #fefefe' }}>
                    <Image src={product.detailUrl} style={{ marginBottom: '15px' }} /><br />
                </Box>
                <StyledButton onClick={()=>addItemToCart()} style={{ marginRight: 10, background: '#ff9f00' }} variant="contained"><Cart />Add to Cart</StyledButton> 
                <StyledButton onClick={()=>buyNow()} style={{ background: '#fb641b' }} variant="contained"><Flash /> Buy Now</StyledButton> 
            </LeftContainer>
        );
    }

    export default ActionItem;


